#! /usr/bin/env pythonimport sys, os, time, datetime, getoptfrom numpy import array#import componentsdef parsePath(path):	# check if file exists	if os.path.isfile(path):		parse_file = file(path,'r')		data = parseFile( parse_file,path )	elif os.path.isdir(path):		for sub_path in os.listdir(path):			data = parsePath(os.path.join(path,sub_path))	return data			def parseFile(parse_file,path):	data = list()	# parse file	for line in parse_file.readlines():		text = line[:-3]		data.append( array( [ float(i) for i in text.split(" ") ] ) )	return array(data)	def getData(dataset=''):	return parsePath(os.path.join('Santa_Fe_Competition',dataset))	 '''	def parseSVM(path):	parse_file = file(path,'r')	data = list()	# parse file	for line in parse_file.readlines():		text = line.split(' ')				data.append( array( [ float(i) for i in text.split(" ") ] )[:2] )	return data'''	